import React from "react";
import { useQuery, gql } from "@apollo/client";
import ReactMarkdown from "react-markdown";

import Wave from "../components/Wave/Wave";
import Navigation from "../components/Navigation/Navigation";
import Skill from "../components/Skill/Skill";
import "./About.scss";

const ABOUT = gql`
  query GetAbout {
    about {
      data {
        attributes {
          about
        }
      }
    }
  }
`;

export default function About() {
  const { loading, error, data } = useQuery(ABOUT);
  if (error) return console.log(error);

  return (
    <>
      <header aria-label="primary">
        <Navigation />
      </header>
      <Wave height="2rem" />
      <main
        style={{ backgroundColor: "var(--clr-primary-400)", padding: "3rem 0" }}
      >
        <section className="container container--bio">
          <h2 className="bio__title">&#128214; - About Me</h2>
          {loading ? (
            <h2>Loading...</h2>
          ) : (
            <ReactMarkdown children={data.about.data.attributes.about} />
          )}
        </section>

        <aside className="container container--skills">
          <Skill skill="html" />
          <Skill skill="css" />
          <Skill skill="js" />
          <Skill skill="sass" />
          <Skill skill="react" />
          <Skill skill="firebase" />
          <Skill skill="graphql" />
          <Skill skill="git" />
        </aside>
      </main>
      <Wave flipped={true} />
    </>
  );
}
