import "./Contact.scss";
import Button from "../Button/Button";
import Wave from "../Wave/Wave";

export default function Contact() {
  const handleSubmit = (e) => {
    e.preventDefault();
    let formData = new FormData(e.currentTarget);
    e.currentTarget.reset();

    // send form data to endpoint
    fetch("https://getform.io/f/1725bee0-1904-4ab0-9ddb-77b4d9ce1876", {
      method: "POST",
      body: formData,
    })
      .then((response) => console.log(response))
      .catch((error) => console.log(error));
  };

  return (
    <section className="contact">
      <h2 className="contact__title">&#128232; - Get In Touch</h2>

      <Wave />
      <form className="form" onSubmit={handleSubmit} id="form">
        <div className="container--input">
          <label htmlFor="name">Name</label>
          <input type="text" id="name" name="name" className="form__name" />
        </div>

        <div className="container--input">
          <label htmlFor="email">Email</label>
          <input type="text" id="email" name="email" className="form__email" />
        </div>

        <div className="container--input">
          <label htmlFor="message">Message</label>
          <textarea
            type="text"
            id="message"
            name="message"
            className="form__message"
            rows="6"
          />
        </div>

        <Button className="form__submit" type="submit">
          Send
        </Button>
      </form>
      <Wave flipped="true" />
    </section>
  );
}
