import React, { useState } from "react";
import "./Hamburger.scss";

function Hamburger() {
  const [isActive, setActive] = useState("false");
  const toggleClass = () => {
    setActive(!isActive);
  };
  return (
    <button
      className={`hamburger hide-for-desktop ${isActive ? "" : "nav-open"}`}
      aria-expanded={`${isActive}`}
      onClick={toggleClass}
    >
      <span></span>
      <span></span>
      <span></span>
    </button>
  );
}

export default Hamburger;
